```html
<html>

<body>
    <button id="replay"></button>
    <button id="undo"></button>
</body>
<script>
    var Ryu = {
        attack: function () {
            console.log('');
        },
        defense: function () {
            console.log('');
        },
        jump: function () {
            console.log('');
        },
        crouch: function () {
            console.log('');
        }
    };

    var makeCommand = function (receiver, state) {  // 
        if (!state) {
            return false;
        }
        return function () {
            receiver[state]();
        }
    };

    var commands = {
        "119": "jump",//w
        "115": "crouch",//s
        "97": "defense",//a
        "100": "attack"//d
    };

    var commandStack = [];  // 

    document.onkeypress = function (ev) {
        var keyCode = ev.keyCode;
        var command = makeCommand(Ryu, commands[keyCode]);
        if (command) {
            command();
            commandStack.push(command);
        }
    }

    document.getElementById('replay').onclick = function () { // 
        var command;
        while (command = commandStack.shift()) {
            command();
        }
    };

    document.getElementById('undo').onclick = function () {
        var command;
        commandStack.pop();//
        while (command = commandStack.shift()) {
            command();
        }
    };
</script>

</html>
```

