 ui_tests/test1.js
```
var webdriver = require('selenium-webdriver'),
    By = webdriver.By,
    until = webdriver.until,
    test = require('selenium-webdriver/testing');

test.describe('1,firefox', function() {
    this.timeout(15000);
    var driver;

    test.before(function() {
        driver = new webdriver.Builder().forBrowser('firefox').usingServer('http://localhost:4444/wd/hub').build();
    });

    test.it('node.js', function() {
        driver.get('http://www.baidu.com');
        driver.findElement(By.name('wd')).sendKeys('node.js');
        driver.findElement(By.id('su')).click();
        driver.wait(until.titleIs('node.js_'), 10000);
    });

    test.it('jquery', function() {
        driver.get('http://www.baidu.com');
        driver.findElement(By.name('wd')).sendKeys('jquery');
        driver.findElement(By.id('su')).click();
        driver.wait(until.titleIs('jquery_'), 10000);
    });

    test.it('angular', function() {
        driver.get('http://www.baidu.com');
        driver.findElement(By.name('wd')).sendKeys('angular');
        driver.findElement(By.id('su')).click();
        driver.wait(until.titleIs('angular_'), 10000);
    });

    test.after(function() {
        driver.quit();
    });
});
```

